import { Component, OnInit } from '@angular/core';
import { CrearFacturaRequest } from 'src/app/models/crearFacturaRequest';
import { DetalleFactura } from 'src/app/models/detalleFactura';

@Component({
  selector: 'app-CrearFactura',
  templateUrl: './CrearFactura.component.html',
  styleUrls: ['./CrearFactura.component.css']
})
export class CrearFacturaComponent implements OnInit {

  constructor() { }

  crearFactura: CrearFacturaRequest = {

    fechaEmisionFactura: new Date(),
    idCliente: 0,
    numeroFactura: 0,
    numeroTotalArticulos: 0,
    subTotalFacturas: 0,
    totalImpuestos: 0,
    totalFactura: 0,
    detalleFactura: []
  };

  detalleFactura: DetalleFactura = {
    idProducto: 0,
    cantidadDeProducto: 0,
    precioUnitarioProducto: 0,
    subtotalProducto: 0,
    notas: ''
  }

  ngOnInit() {
  }

}
<div class="container mt-5">
  <h2>Crear Factura</h2>

  <form>

    <div class="row">

      <div class="form-group">
        <label for="idCliente">Cliente</label>
        <select class="form-control" id="idCliente">
          <option>1</option>
          <option>2</option>
          <option>3</option>
          <option>4</option>
          <option>5</option>
        </select>
      </div>

      <div class="form-group">
        <label for="numeroFactura">Numero de Factura</label>
        <input type="text" id="NumeroFactura" class="form-control" [(ngModel)]="crearFactura.numeroFactura">
      </div>

    </div>
  </form>
</div>
